const newYears="1 Jan 2022",daysEl=document.getElementById("days"),hoursEl=document.getElementById("hours"),minsEl=document.getElementById("mins"),secondsEl=document.getElementById("seconds");function countTime(){const e=new Date,n=(new Date(newYears)-e)/1e3,t=Math.floor(n/3600/24),o=Math.floor(n/3600)%24,r=Math.floor(n/60)%60,s=Math.floor(n)%60;daysEl.innerHTML=timeCorrect(t),hoursEl.innerHTML=timeCorrect(o),minsEl.innerHTML=timeCorrect(r),secondsEl.innerHTML=timeCorrect(s)}function timeCorrect(e){return e<10?"0"+e:e}setInterval(countTime,1e3);